import axios from 'axios';

const apiUrl = "http://localhost:7216"
// Set default API address using Config Defaults
axios.defaults.baseURL = "http://localhost:7216";

// Add an error interceptor
axios.interceptors.response.use(
  (response) => response,
  (error) => {
    // Log the error to the console or any logging service
    console.error('Axios Error Interceptor😵‍💫:', error);
    return Promise.reject(error);
  }
);

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/todoitems`)    
    return result.data;
  },

  addTask: async (name) => {
    try {
      const result = await axios.post(`${apiUrl}/todoitems`, { Name: name, IsComplete: false });
      return result.data;
    } catch (error) {
      console.error('Error adding task:', error);
      throw error;
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      const result = await axios.put(`${apiUrl}/todoitems/${id}`, { IsComplete: isComplete });
      return result.data;
    } catch (error) {
      console.error('Error setting completion status:', error);
      throw error;
    }
  },

  deleteTask: async (id) => {
    try {
      const result = await axios.delete(`${apiUrl}/todoitems/${id}`);
      return result.data;
    } catch (error) {
      console.error('Error deleting task:', error);
      throw error;
    }
  }
};
